<?phpinclude("config.php");require_once 'lib/torrent.php';error_reporting(E_ALL ^ E_NOTICE);$xml = new XmlWriter();$xml->openMemory();$xml->startDocument('1.0', 'UTF-8');$xml->startElement('games');function write(XMLWriter $xml, $data){    foreach($data as $key => $value){        if(is_array($value)){            if(substr($key,0,5) == 'game_'){ $key = 'game'; }            if(substr($key,0,3) == 'exe' && $key != 'exes'){ $key = 'exe'; }            $xml->startElement($key);            write($xml, $value);            $xml->endElement();            continue;        }        $xml->writeElement($key, $value);    }}$gameId = 0;foreach(glob(GAMES."*.torrent") as $torrent){  $name = trim(basename($torrent,'.torrent'));  $txt = str_replace('.torrent','.txt',$torrent);  if(!file_exists($txt)){    continue;  }  $exes = file($txt);  $xmlexes = array();  foreach($exes as $exe){    list($exec,$executable,$icon) = explode('#',$exe);    $xmlexes['exe'.$i++] = array("file" => trim($exec), "icon" => trim($icon), "name" => trim($executable));  }  $decoder = new torrent_decoder($torrent);  $info = $decoder->decode();  $size = 0;    if($info['info']['files']){    foreach($info['info']['files'] as $file){        $size += (int)$file['length'];    }  } else {      $size = (int)$info['info']['length'];  }    $output['game_'.($gameId++)] = array("title" => $name, "size" => $size, "message" => $data['message'].' ', "exes" => $xmlexes);}header("Content-Type: text/xml");write($xml, $output);$xml->endElement();echo $xml->outputMemory(true);