<?phpinclude("config.php");error_reporting(E_ALL ^ E_NOTICE);$xml = new XmlWriter();$xml->openMemory();$xml->startDocument('1.0', 'UTF-8');$xml->startElement('games');$db = new SQLite3('db/playing.db');// remove old data$db->exec("DELETE FROM players WHERE timestamp < ".(time()-600));function write(XMLWriter $xml, $data){    foreach($data as $key => $value){        if(is_array($value)){            if(substr($key,0,5) == 'game_'){ $key = 'game'; }            if(substr($key,0,3) == 'exe' && $key != 'exes'){ $key = 'exe'; }            $xml->startElement($key);            write($xml, $value);            $xml->endElement();            continue;        }        $xml->writeElement($key, $value);    }}$gameId = 0;foreach(glob(GAMES."*.torrent") as $torrent){  $name = trim(basename($torrent,'.torrent'));  $txt = str_replace('.torrent','.txt',$torrent);  if(!file_exists($txt)){    continue;  }  $size = 0;  $exes = file($txt);  $xmlexes = array();  $first = true;  foreach($exes as $exe){    if($first){      $size = $exe;      $first = false;      continue;    }    list($exec,$executable,$icon) = explode('#',$exe);    $xmlexes['exe'.$i++] = array("file" => trim($exec), "icon" => trim($icon), "name" => trim($executable));  }    $result = $db->query("SELECT nickname  FROM players WHERE game = '".$name."'");  $players = array();  while($row = $result->fetchArray()){    $players[] = $row['nickname'];  }  $output['game_'.($gameId++)] = array("title" => $name, "size" => $size, "message" => $data['message'].' ', "exes" => $xmlexes, "players" => implode(', ',$players));}header("Content-Type: text/xml");write($xml, $output);$xml->endElement();echo $xml->outputMemory(true);